mapper.select:
select usr_id, usr_login, usr_password
from SCRUM_USER
where usr_login = #{login}
	AND usr_password = #{password}

select user_id, usr_login, usr_password
from SCRUM_USER
where usr_id = #{id}

mapper.insert:
INSERT INTO SCRUM_USER (
usr_login,
usr_password
)
VALUES (
#{user.login},
#{user.password}
)
